import React, { useEffect, useState } from 'react';
import { getTasks, createTask } from '../services/Api'; // Import getTasks dan createTask dari Api.js
import './task.module.css'; // Import CSS

const PersonalTaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [taskDescription, setTaskDescription] = useState('');
  const [status, setStatus] = useState('Not Started');
  const [dueDate, setDueDate] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = () => {
    getTasks()
      .then(response => {
        setTasks(response);
      })
      .catch(error => {
        console.error('There was an error fetching the tasks!', error);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'deskripsi') {
      setTaskDescription(value);
    } else if (name === 'status') {
      setStatus(value);
    } else if (name === 'dueDate') {
      setDueDate(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const userId = localStorage.getItem("user_id");
      if (!token) {
        alert("You need to be logged in to create a task");
        return;
      }
      if (!userId) {
        alert("User ID not found");
        return;
      }
      const newTask = {
        user_id: userId,
        task_description: taskDescription,
        status: status,
        due_date: new Date(dueDate).toISOString().slice(0, 19)
      };
      console.log("Task Data:", newTask);
      await createTask(newTask);
      fetchTasks(); // Refresh task list
      setTaskDescription('');
      setStatus('Not Started');
      setDueDate('');
      alert("Task created successfully");
    } catch (error) {
      console.error("Error creating task:", error);
      alert("Error creating task");
    }
  };

    createTask(newTask)
      .then(() => {
        fetchTasks();
        setTaskDescription('');
        setStatus('Not Started');
        setDueDate('');
      })
      .catch(error => {
        console.error('There was an error creating the task!', error);
      });

  return (
    <div className="container">
      <div className="left-panel">
        <h1>Create Task</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="taskDescription">Task Description:</label>
            <input
              type="text"
              name="deskripsi"
              value={taskDescription}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="status">Status:</label>
            <select
              id="status"
              name="status"
              value={status}
              onChange={handleChange}
              required
            >
              <option value="Not Started">Not Started</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>
          <div>
            <label htmlFor="dueDate">Due Date:</label>
            <input
              type="datetime-local"
              id="dueDate"
              name="dueDate"
              value={dueDate}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
      <div className="right-panel">
        <h1>Personal Tasks</h1>
        <ul>
          {tasks.map(task => (
            <li key={task.task_id}>
              <h2>{task.task_description}</h2>
              <p><span>Due Date:</span> {new Date(task.due_date).toLocaleDateString()}</p>
              <p><span>Status:</span> {task.status}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PersonalTaskList;
